mapper.update:
update user
username = #{username}, password = #{password}, sex = #{sex}, phone = #{phone}, email = #{email}, nickname = #{nickname}, birthday = #{birthday}
where id = #{id}

mapper.select:
select *
from user

    and id = #{id}
    and username = #{username}
    and password = #{password}
    and sex = #{sex}
    and phone = #{phone}
    and email = #{email}
    and nickname = #{nickname}
    and birthday = #{birthday}
    and createdate = #{createdate}
