(function(add, a) {
	add.prototype.mouseRight = function(c, h) {
		//cls class
		var f, cls, e, g;
		e = this;
		f = {
			menu: [{}, ],
			ele: "wrap-ms-right",
			ele_item: "ms-item",
		};
		cls = add.extend({}, f, c);


		add(this)
			.each(function() {
				(function i() {
					var n = add("<div></div>");
					n.attr("class", cls.ele);
					//data JSON 数据
					// icon 图标
					for (var data = 0; data < cls.menu.length; data++) {
						icon = cls.menu[data].icon;
						var k = add("<li></li>");
						k.addClass(cls.ele_item);
						var l = add("<i>"+icon+"</i>");
						k.attr("data-item", data);
						l.addClass(cls.menu[data].className);
						l.attr("data-item", data);
						l.appendTo(k);
						k.appendTo(n);
						l.after("&nbsp; " + cls.menu[data].itemName)
					}
					n.prependTo("body");
					var j = add("<div></div>");
					j.attr("class", "shade");
					j.prependTo("body")
				})();
				a.oncontextmenu = function() {
					return false
				};
				add(this)
					.mousedown(function(j) {
						if (j.which === 3) {
							add("." + cls.ele)
								.css({
									"display": "block",
									"top": j.pageY + "px",
									"left": j.pageX + "px"
								})
						}
					});
				add("." + cls.ele)
					.click(function(l) {
						var j = parseInt(add(l.target)
							.attr("data-item"));
						for (var k = 0; k < cls.menu.length; k++) {
							if (j == k) {
								cls.menu[k].callback();
								add("." + cls.ele)
									.css({
										"display": "none"
									})
							}
						}
					});
				add(".shade")
					.click(function() {
						add("." + cls.ele)
							.css({
								"display": "none"
							})
					})
			});
		return this
	}
})(jQuery, window);
